    //     RemovePuppy={removePuppyHandler}
    //     editingPuppy={editingPuppy}
    //     setEditingPuppy={setEditingPuppy}
    //     cancel={() => {
    //       setEditingPuppy(false);
    //       setPuppyForm(false);
    //     }}
    //   />
    // ) : (
    <div className="litter-form">
      {/* "handleSubmit" will validate your inputs before invoking "onSubmit" */}
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="litter-info">
          <div className="row">
            <label>
              Owner's Name:{" "}
              <input
                name="ownerName"
                placeholder="Owner's Name"
                onChange={handleFormChange}
                defaultValue={litterFormData.ownerName}
                ref={register}
              />
            </label>
            {errors.dam && <span>this field is required:</span>}
            <label>
              Dam:{" "}
              <input
                name="dam"
                placeholder="Dam"
                type="text"
                list="dams"
                onChange={handleFormChange}
                defaultValue={litterFormData.dam}
                ref={register({ required: false })}
              />
              {
                //todo datalist populate from past entries
              }
              <datalist id="dams">
                <option>Rose</option>
                <option>Sadie</option>
                <option>Athena</option>
                <option>Darby</option>
              </datalist>
            </label>
            {errors.sire && <span>this field is required:</span>}
            <label>
              Sire:{" "}
              <input
                name="sire"
                placeholder="Sire"
                type="text"
                list="sires"
                onChange={handleFormChange}
                defaultValue={litterFormData.sire}
                ref={register({ required: false })}
              />
              <datalist id="sires">
                <option>Rodeo</option>
                <option>Cowboy</option>
                <option>Birch</option>
                <option>Rosco</option>
              </datalist>
            </label>
          </div>
          <div className="row">
            {errors.litterDate && <span>this field is required:</span>}
            {/* //todo date format/validation MM/DD/YY  */}
            <div className="date">
              <label>
                Breed Date:{" "}
                <input
                  name="breedDate"
                  type="date"
                  onChange={handleFormChange}
                  defaultValue={litterFormData.breedDate}
                  ref={register({ required: false })}
                />
              </label>
              <p>
                Expected Date:{" "}
                {expectedDateHandler("breedDate", 63, "expectedDate")}
              </p>
            </div>
            <div className="date">
              <label>
                Litter Date:{" "}
                <input
                  name="litterDate"
                  type="date"
                  onChange={handleFormChange}
                  defaultValue={litterFormData.litterDate}
                  ref={register({ required: false })}
                />
              </label>
              <p>New Home: {expectedDateHandler("litterDate", 56)}</p>
            </div>
          </div>
        </div>
        {/* <table className="puppies">
          <thead>
            <tr>
              <th>id</th>
              <th>name</th>
              <th>sex</th>
              <th>markings</th>
              <th>Name</th>
              <th>phone</th>
              <th>price</th>
              <th>paymentValue</th>
              <th>paymentType</th>
              <th>fees</th>
              <th>sellerPayment</th>
              <th>commission</th>
              <th>AKC</th>
              <th>listed</th>
            </tr>
          </thead>
          <tbody>
            {puppies
              ?.sort((a, b) => a.id - b.id)
              .map(
                ({
                  id,
                  sex,
                  address,
                  akcRegistered,
                  commission,
                  fees,
                  firstName,
                  lastName,
                  listed,
                  markings,
                  name,
                  phone,
                  paymentType,
                  paymentValue,
                  price,
                  sellerPayment,
                }) => (
                  <tr className="puppy">
                    <td>{id}</td>
                    <td>{name}</td>
                    <td>{sex}</td>
                    <td>{markings}</td>
                    <td>
                      {firstName} {lastName}
                    </td>
                    <td>{phone}</td>
                    <td>{price}</td>
                    <td>{paymentValue}</td>
                    <td>{paymentType}</td>
                    <td>{fees}</td>
                    <td>{sellerPayment}</td>
                    <td>{commission}</td>
                    <td>{akcRegistered ? "Yes" : "No"}</td>
                    <td>{listed ? "Yes" : "No"}</td>
                    <button onClick={() => editPuppyHandler(id)}>Edit</button>
                  </tr>
                )
              )}
          </tbody>
        </table> */}
        {/* <button
          onClick={() => {
            // setNewPuppyId(newPuppyId + 1);
            setPuppyForm(true);
          }}
        >
          Add Puppy
        </button> */}
        <input type="submit" value="Next" />
      </form>
      {/* <PuppyForm
        newPuppyId={newPuppyId}
        puppies={puppies}
        setPuppies={setPuppies}
        setPuppyForm={setPuppyForm}
        RemovePuppy={removePuppyHandler}
        editingPuppy={editingPuppy}
        setEditingPuppy={setEditingPuppy}
        cancel={() => {
          setEditingPuppy(false);
          setPuppyForm(false);
        }}
      /> */}
    </div>
  );

  function expectedDateHandler(dateFrom, days, dataField) {
    if (watch(dateFrom)) {
      const baseDate = watch(dateFrom).split("-");
      let date = new Date(baseDate[0], --baseDate[1], baseDate[2]);
      let expectedDate = new Date();
      expectedDate.setDate(date.getDate() + days);
      expectedDate = expectedDate.toISOString().slice(0, 10);
      // console.log({ baseDate, date, expectedDate });
      return expectedDate;
    }
    return "N/A";
  }
  function editPuppyHandler(id) {
    setEditingPuppy(puppies.find((puppy) => puppy.id === id));
    setPuppyForm(true);
  }
  function removePuppyHandler(id) {
    // todo shift id's of other puppies
    console.log("REMOVE", id);
    setPuppies(puppies.filter((puppy) => puppy.id !== id));
  }
  function handleFormChange(evt) {
    setLitterFormData({
      ...litterFormData,
      [evt.target.name]: evt.target.value,
    });
  }
});
